import React, { useState, useEffect } from "react";
import "./car.css";
import CategoriesNav from "../categoriesNav/categoriesNav";


function filterAndStoreByCategory(inputList, category, outputList) {
  outputList.length = 0;
  inputList.forEach(item => {
    if (item.category === category) {
      outputList.push(item);
    }
  });
}

const Car = () => {
  const [data, setData] = useState([]); 
  const [filteredData, setFilteredData] = useState([]);
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

 
  const fetchData = async (category) => {
    try {
      const response = await fetch("https://demo.kwtmarkets.com/");
      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }
      const result = await response.json();
  
      // تصفية البيانات حسب الفئة
      const filteredCars = [];
      filterAndStoreByCategory(result, category, filteredCars);
      setData(filteredCars);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };
  

  useEffect(() => {
    fetchData(); 
  }, []);

  return (
    <div className="car">
      <CategoriesNav/>
      <h2 className="car-title">Available Cars</h2>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>} 

      <div className="car-container">
        {data.map((car) => (
          <div key={car.id} className="car-card">
            <img src={car.image} alt={car.name} className="car-image" />
            <div className="car-content">
              <h3 className="car-name">{car.name}</h3>
              <p className="car-description">{car.description}</p>
              <p className="car-price">Price: {car.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Car;
