import React, { useState, useEffect } from 'react';
import './recommendedSection.css';

const RecommendedProducts = () => {
  const [allCategories, setAllCategories] = useState([]);
  const [displayedCategories, setDisplayedCategories] = useState([]);
  
  // جلب الفئات والمنتجات من API
  const fetchData = async () => {
    try {
      const response = await fetch('https://demo.kwtmarkets.com/items');
      const categories = await response.json();
      setAllCategories(categories); // تخزين جميع الفئات
      setDisplayedCategories(getRandomCategories(categories, 4)); // عرض 4 فئات عشوائيًا
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  // دالة لاختيار عدد عشوائي من الفئات
  const getRandomCategories = (data, count) => {
    const shuffled = [...data].sort(() => Math.random() - 0.5); // ترتيب عشوائي
    return shuffled.slice(0, count); // أخذ العدد المحدد من الفئات
  };

  useEffect(() => {
    fetchData(); // جلب البيانات عند تحميل الصفحة
  }, []);

  // عرض المزيد من المنتجات عشوائيًا عند الضغط على See More
  const handleSeeMore = () => {
    setDisplayedCategories(getRandomCategories(allCategories, allCategories.length)); // عرض كل الفئات بشكل عشوائي
  };

  return (
    <div className="recommended-products">
      <h2>All Recommended Products</h2>
      <div className="product-grid">
        {displayedCategories.map((category) => (
          <div className="product-card" key={category.id}>
            <img 
              src={category.images || 'default-image-url.jpg'} 
              alt={category.title || 'Product'} 
              className="product-image" 
            />
            <div className="product-details">
              <h3>{category.title || 'No Name'}</h3>
              <p>{category.description || 'No Description'}</p>
              <p>{category.price || 'No Price'}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="section-header">
        <a href="#" onClick={handleSeeMore} className="see-more">See More {'>'}</a>
      </div>
    </div>
  );
};

export default RecommendedProducts;
