import React, { useState, useEffect } from "react";
import "./car.css";
import CategoriesNav from "../categoriesNav/categoriesNav";

// دالة لتصفية البيانات حسب الفئة
function filterAndStoreByCategory(inputList, category) {
  return inputList.filter(item => item.category === category);
}

const Car = () => {
  const [ setData] = useState([]); 
  const [filteredData, setFilteredData] = useState([]); // قائمة الفئات المفلترة
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

  // جلب البيانات من الـ API
  const fetchData = async () => {
    try {
      const response = await fetch("https://demo.kwtmarkets.com/items");
      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }
      let result = await response.json();
      setData(result); 

      let filtered = filterAndStoreByCategory(result, "Cars"); // تصفية البيانات حسب فئة معينة
      setFilteredData(filtered); // تحديث حالة البيانات المفلترة
    } catch (err) {
      setError(err.message); 
    } finally {
      setLoading(false); 
    }
  };

  useEffect(() => {
    fetchData(); 
  }, []);

  return (
    <div className="car">
      <CategoriesNav />
      <h2 className="car-title">Available Cars</h2>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>} 

      <div className="car-container">
        {filteredData.map((car) => (
          <div key={car.id} className="car-card">
            <img src={car.images} alt={car.name} className="car-image" />
            <div className="car-content">
              <h3 className="car-title">title:{car.title}</h3>
              <p className="car-description">description:{car.description}</p>
              <p className="car-price">Price: {car.price}</p>
              <p className="car-location">location: {car.location}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Car;
