import React from 'react';
import './recommendedSection.css';

const RecommendedSection = ({ products, handleProductClick }) => {
  console.log("Products received in RecommendedSection:", products);
  return (
    <div className="recommended-section">
      <h2>Recommended For You</h2>
      <div className="section-header">
        <a href="/recommended-products" className="see-more">See More {'>'} </a>
      </div>
      <div className="product-grid">
        {products.map((product) => (
          <div
            className="product-card"
            key={product._id} // استخدام _id بدلاً من id
            onClick={() => handleProductClick(product._id)} // تحديث المعرف
          >
            <img 
              src={(product.images && product.images.length > 0) ? product.images[0] : 'default-image-url.jpg'} 
              alt={product.title || 'Product'} 
              className="product-image" 
            />
            <div className="product-details">
              <h3>{product.title || 'No Title'}</h3>
              <p className="days">{product.days || 'N/A'} Days</p>
              <p className="status">
                <span className="for-sale">For Sale</span> {product.description || 'No Description'}
              </p>
              <p className="price">{product.price ? `${product.price} KWD` : 'Price not available'}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedSection;
