import React, { useState, useEffect } from 'react';
import './recommendedSection.css';

const RecommendedSection = ({ handleProductClick }) => {
  const [randomCategories, setRandomCategories] = useState([]);
  const [allCategories, setAllCategories] = useState([]);

  // جلب الفئات والمنتجات من API
  const fetchData = async () => {
    try {
      const response = await fetch('https://demo.kwtmarkets.com/items');
      const categories = await response.json();
      setAllCategories(categories); // تخزين جميع الفئات
      setRandomCategories(getRandomCategories(categories, 4)); // اختيار 4 فئات عشوائية
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  // دالة لاختيار 4 فئات عشوائية
  const getRandomCategories = (data, count = 4) => {
    const shuffled = [...data].sort(() => Math.random() - 0.5); // ترتيب عشوائي
    return shuffled.slice(0, count); // أخذ أول 4 فئات
  };

  useEffect(() => {
    fetchData(); // جلب البيانات عند تحميل الصفحة
  }, []);

  return (
    <div className="recommended-section">
      <h2>Recommended For You</h2>
      <div className="section-header">
        <a href="/recommended-products" className="see-more">See More {'>'}</a>
      </div>
      <div className="category-grid">
        {randomCategories.map((category) => (
          <div
            className="category-card"
            key={category.id} // تأكد من استخدام المعرف الصحيح
            onClick={() => handleProductClick(category.id)} 
          >
            <img 
              src={category.images || 'default-image-url.jpg'} 
              alt={category.title|| 'Category'} 
              className="category-image" 
            />
            <div className="category-details">
              <h3>{category.title || 'No Name'}</h3>
              <p className="description">{category.description || 'No Description'}</p>
              <p className="price">{category.price || 'No price'}</p>
              <p className="location">{category.location || 'No location'}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecommendedSection;
