import React, { useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate } from "react-router-dom";  // استيراد useNavigate للتوجيه بين الصفحات
import PhoneInput from "react-phone-input-2";
import "react-phone-input-2/lib/style.css";
import "./SignUp.css";
import Logo from "../../assets/kwait-removebg 1.png";
import { signUpUser, checkPhoneExistence } from "../../Api/config";  // استيراد الدوال اللازمة من التكوين

const SignUp = () => {
  const { t } = useTranslation();
  const [phoneNumber, setPhoneNumber] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSignUp = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setMessage("");

    if (!name || !email || !phoneNumber || !password) {
      setError(t("error_fill_all_fields"));
      setLoading(false);
      return;
    }

    const formattedPhoneNumber = phoneNumber.startsWith("+") ? phoneNumber : `+${phoneNumber}`;

    try {
      const phoneCheck = await checkPhoneExistence(formattedPhoneNumber);
      if (phoneCheck.exists) {
        setError(t("error_phone_registered"));
        return;
      }

      const response = await signUpUser({
        phoneNumber: formattedPhoneNumber,
        name,
        email,
        password,
      });

      console.log("Registration Successful:", response);
      setMessage(t("success_registration"));
      navigate("/login");  // توجيه المستخدم إلى صفحة الدخول بعد التسجيل الناجح
    } catch (err) {
      setError(err.response?.data?.message || t("error_registration"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-box">
        <img src={Logo} alt={t("logo_alt")} className="logo" />
        <h2>{t("signup_title")}</h2>
        <p>{t("signup_description")}</p>
        {error && <p className="error-message">{error}</p>}
        {message && <p className="success-message">{message}</p>}
        <form onSubmit={handleSignUp}>
          <div className="input-group">
            <PhoneInput
              country={"kw"}
              value={phoneNumber}
              onChange={(value) => setPhoneNumber(value)}
              inputStyle={{
                width: "100%",
                borderRadius: "4px",
                border: "1px solid #ddd",
                padding: "19px",
                paddingLeft: "50px",
              }}
            />
          </div>
          <div className="input-group">
            <input
              type="text"
              placeholder={t("placeholder_name")}
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <input
              type="email"
              placeholder={t("placeholder_email")}
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <input
              type="password"
              placeholder={t("placeholder_password")}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="signup-btn" disabled={loading}>
            {loading ? t("loading") : t("sign_up")}
          </button>
        </form>
      </div>
    </div>
  );
};

export default SignUp;
