import React, { useState, useEffect } from 'react';
import './animalsSection.css';
import { Link } from 'react-router-dom';
import { fetchData } from '../../OurCategories/dataApi'; // استيراد الدالة

const AnimalsSection = ({ handleAnimalClick }) => {
  const [Animals, setAnimals] = useState([]);

  // هنا نقوم بجلب البيانات عند تحميل الكومبوننت
  useEffect(() => {
    const fetchAnimals = async () => {
      try {
        const data = await fetchData('Animals'); // تصفية البيانات حسب فئة Animals
        setAnimals(data); // تخزين البيانات في state
      } catch (error) {
        console.error('Error fetching Animals:', error);
      }
    };

    fetchAnimals();
  }, []); // يتم تنفيذ الـ useEffect عند أول تحميل للكومبوننت فقط

  return (
    <div className="animals-section">
      <h2>All in Animals</h2>
      <div className="section-header">
        <Link to="/Animal" className="see-more">See More {'>'} </Link>
      </div>
      <div className="Animal-grid">
        {Animals.length === 0 ? (
          <p>Loading...</p> // عرض رسالة تحميل بينما البيانات قيد الجلب
        ) : (
          Animals.map((Animal) => (
            <div
              className="Animal-card"
              key={Animal.id}
              onClick={() => handleAnimalClick(Animal.id)}
            >
              <img src={Animal.images} alt={Animal.title} className="Animal-image" />
              <div className="Animal-details">
                <h3>{Animal.title}</h3>
                <p className="days">{Animal.days} Days</p>
                <p className="status">
                  <span className="for-sale">For Sale</span> {Animal.description}
                </p>
                <p className="price">{Animal.price} </p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AnimalsSection;
