import React, { useState, useEffect } from "react";
import "./Furniture.css";
import CategoriesNav from "../categoriesNav/categoriesNav";

const Furniture = () => {
  const [data, setData] = useState([]); 
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null);

 
  const fetchData = async () => {
    try {
      const response = await fetch("https://api.example.com/Furniture"); 
      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }
      const result = await response.json();
      setData(result); 
    } catch (err) {
      setError(err.message); 
    } finally {
      setLoading(false); 
    }
  };

  useEffect(() => {
    fetchData(); 
  }, []);

  return (
    <div className="Furniture">
      <CategoriesNav/>
      <h2 className="Furniture-title">Available Furniture</h2>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>} 

      <div className="Furniture-container">
        {data.map((furniture) => (
          <div key={furniture.id} className="Furniture-card">
            <img src={furniture.image} alt={furniture.name} className="Furniture-image" />
            <div className="Furniture-content">
              <h3 className="Furniture-name">{furniture.name}</h3>
              <p className="Furniture-description">{furniture.description}</p>
              <p className="Furniture-price">Price: {furniture.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Furniture;
