import React, { useState, useEffect } from 'react';
import './animalsSection.css';
import { Link } from 'react-router-dom';
import { fetchData } from '../../api'; // استيراد الدالة

const AnimalsSection = ({ handleProductClick }) => {
  const [products, setProducts] = useState([]);

  // هنا نقوم بجلب البيانات عند تحميل الكومبوننت
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const data = await fetchData('Animals'); // تصفية البيانات حسب فئة Animals
        setProducts(data); // تخزين البيانات في state
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []); // يتم تنفيذ الـ useEffect عند أول تحميل للكومبوننت فقط

  return (
    <div className="animals-section">
      <h2>All in Animals</h2>
      <div className="section-header">
        <Link to="/Animal" className="see-more">See More {'>'} </Link>
      </div>
      <div className="product-grid">
        {products.length === 0 ? (
          <p>Loading...</p> // عرض رسالة تحميل بينما البيانات قيد الجلب
        ) : (
          products.map((product) => (
            <div
              className="product-card"
              key={product.id}
              onClick={() => handleProductClick(product.id)}
            >
              <img src={product.image} alt={product.title} className="product-image" />
              <div className="product-details">
                <h3>{product.title}</h3>
                <p className="days">{product.days} Days</p>
                <p className="status">
                  <span className="for-sale">For Sale</span> {product.description}
                </p>
                <p className="price">{product.price} KWD</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default AnimalsSection;
