import React, { useState, useEffect } from 'react';
import './furnitureSection.css';
import { Link } from 'react-router-dom';
import { fetchData } from '../../OurCategories/dataApi';// استيراد دالة fetchData

const FurnitureSection = ({ handleProductClick }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // جلب المنتجات الخاصة بالأثاث
  useEffect(() => {
    const fetchFurniture = async () => {
      try {
        const result = await fetchData('Furniture'); // تصفية البيانات حسب فئة الأثاث
        setProducts(result);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchFurniture();
  }, []);

  return (
    <div className="furniture-section">
      <h2>All in Furniture</h2>
      <div className="section-header">
        <Link to="/Furniture" className="see-more">See More {'>'} </Link>
      </div>
      <div className="product-grid">
        {loading && <p>Loading...</p>}
        {error && <p style={{ color: "red" }}>{error}</p>}
        {products.map((product) => (
          <div
            className="product-card"
            key={product.id}
            onClick={() => handleProductClick(product.id)}
          >
            <img src={product.image} alt={product.title} className="product-image" />
            <div className="product-details">
              <h3>{product.title}</h3>
              <p className="days">{product.days} Days</p>
              <p className="status">
                <span className="for-sale">For Sale</span> {product.description}
              </p>
              <p className="price">{product.price} KWD</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FurnitureSection;
