import React, { useState, useEffect } from 'react';
import './animalsSection.css';
import { Link } from 'react-router-dom';
import { fetchData } from '../../OurCategories/dataApi';

const AnimalsSection = ({ handleAnimalClick }) => {
  const [Animals, setAnimals] = useState([]);

  useEffect(() => {
    const fetchAnimals = async () => {
      try {
        const data = await fetchData('Animals'); // Fetch Animals data
        setAnimals(data);
      } catch (error) {
        console.error('Error fetching Animals:', error);
      }
    };

    fetchAnimals();
  }, []);

  return (
    <div className="animals-section">
      <h2>All in Animals</h2>
      <div className="section-header">
        <Link to="/Animal" className="see-more">See More {'>'} </Link>
      </div>
      <div className="Animal-grid">
        {Animals.length === 0 ? (
          <p>Loading...</p>
        ) : (
          Animals.map((Animal) => (
            <div
              className="Animal-card"
              key={Animal.id}
              onClick={() => handleAnimalClick?.(Animal.id)}
            >
              <img src={Animal.images} alt={Animal.title} className="Animal-image" />
              <div className="Animal-details">
                <h3>{Animal.title}</h3>
                <p className="Animal-description">{Animal.description}</p>
                <p className="Animal-price">Price: {Animal.price}</p>
                <p className="Animal-location">Location: {Animal.location}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

// Default prop for handleAnimalClick
AnimalsSection.defaultProps = {
  handleAnimalClick: () => console.log('Animal clicked, but no handler provided!'),
};

export default AnimalsSection;
