import React, { useState } from 'react';
import './heroSection.css';
import image from '../../assets/Rectangle 3.png';

const HeroSection = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const images = [
    {
      src: image,
      title: 'A Wide Variety of Real Estate',
      description: 'is here where you find your new home with your specific specifications',
    },
    {
      src: image,
      title: 'Find Your Dream Home',
      description: 'Explore a wide selection of homes that match your needs.',
    },
    {
      src: image,
      title: 'Invest in Real Estate',
      description: 'Secure your future with valuable real estate investments.',
    }
  ];

  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  const prevImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + images.length) % images.length);
  };

  return (
    <div className="hero-section">
      <button onClick={prevImage} className="prev-arrow">{`<`}</button>
      <div className="hero-image">
        <img src={images[currentIndex].src} alt="Real Estate" className="image" />
        <div className="hero-text">
          <h1>{images[currentIndex].title}</h1>
          <p>{images[currentIndex].description}</p>
          <button className="explore-btn">Explore Now</button>
        </div>
      </div>
      <button onClick={nextImage} className="next-arrow">{`>`}</button>
    </div>
  );
};

export default HeroSection;
